mapper.select:
select school_id, name, information, status from school where 1 = 1
and school_id = #{school_id}
limit 1

mapper.update:
update school set

    name = #{name},


    information = #{information},


    status = #{status},

school_id = #{school_id}
where school_id = #{school_id}

mapper.select:
select class_id, name, school_id, status from class where 1 = 1
and class_id = #{class_id}
limit 1

mapper.update:
update class set

    name = #{name},


    school_id = #{school_id},


    status = #{status},

class_id = #{class_id}
where class_id = #{class_id}
